// –ü–æ–ª—É—á–µ–Ω–∏–µ —Å–∫–ª–∞–¥–∞ –ü–≠–ö –ø–æ –≥–æ—Ä–æ–¥—É
const getPekWarehouseId = (cityName: string): string | null => {
  const normalizedCity = cityName.toLowerCase()
    .replace(/—ë/g, '–µ')
    .replace(/[\s\-\.–≥\.]+/g, '')
    .replace(/–æ–±–ª–∞—Å—Ç—å|–æ–±–ª|–∫—Ä–∞–π|—Ä–µ—Å–ø—É–±–ª–∏–∫–∞|—Ä–µ—Å–ø|–∞–≤—Ç–æ–Ω–æ–º–Ω—ã–π –æ–∫—Ä—É–≥|–∞–æ/g, '');

  console.log(`üè¢ –ü–æ–∏—Å–∫ —Å–∫–ª–∞–¥–∞ –ü–≠–ö –¥–ª—è –≥–æ—Ä–æ–¥–∞: "${cityName}" -> "${normalizedCity}"`);

  const warehouseId = PEK_WAREHOUSES[normalizedCity];
  if (warehouseId) {
    console.log(`‚úÖ –ù–∞–π–¥–µ–Ω —Å–∫–ª–∞–¥ –ü–≠–ö: ${warehouseId}`);
    return warehouseId;
  }

  console.log(`‚ùå –°–∫–ª–∞–¥ –ü–≠–ö –¥–ª—è –≥–æ—Ä–æ–¥–∞ "${cityName}" –Ω–µ –Ω–∞–π–¥–µ–Ω`);
  return null;
};

