input.newString:
@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

/* Стили для печати */
@media print {
  body {
    background: white !important;
    color: black !important;
  }

  * {
    color: black !important;
    background: white !important;
    border-color: #ccc !important;
  }

  .print\\:hidden {
    display: none !important;
  }

  h1, h2, h3, h4, h5, h6 {
    color: black !important;
  }

  .text-white, .text-blue-400, .text-blue-300, .text-gray-400 {
    color: black !important;
  }

  .bg-gray-800, .bg-gray-900, .bg-blue-900 {
    background: white !important;
    border: 1px solid #ccc !important;
  }
}

