const toggleDetails = (company: string) => {
  setExpandedDetails(prev => ({
    ...prev,
    [company]: !prev[company]
  }));
};

const toggleDebugInfo = (company: string) => {
  setExpandedDebugInfo(prev => ({
    ...prev,
    [company]: !prev[company]
  }));
};

