/**
 * Удаление всех грузов товара
 */
export function removeCargosForProduct(
  cargos: CargoWithMetadata[], 
  productId: string, 
  addedAt: number
): CargoWithMetadata[] {
  return cargos.filter(cargo => {
    return !(cargo.productId === productId && cargo.id.includes(`_${addedAt}`));
  });
}

