/**
 * Поиск индексов грузов, принадлежащих конкретному товару
 */
export function findCargoIndexesForProduct(
  cargos: CargoWithMetadata[], 
  productId: string, 
  addedAt: number
): number[] {
  const indexes: number[] = [];

  cargos.forEach((cargo, index) => {
    if (cargo.productId === productId && 
        (cargo.addedAt === addedAt || cargo.id.includes(`_${productId}_${addedAt}_`))) {
      indexes.push(index);
    }
  });

  return indexes;
}

