// –§—É–Ω–∫—Ü–∏—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∏–∑ Google Sheets
const updateProductData = async () => {
  setIsUpdatingData(true);
  setUpdateStatus('updating');

  try {
    // –í —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ –∑–¥–µ—Å—å –±—ã–ª –±—ã –∑–∞–ø—Ä–æ—Å –∫ Google Sheets API
    // –î–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–∏ –∏–º–∏—Ç–∏—Ä—É–µ–º –ø–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö

    // –ò–º–∏—Ç–∞—Ü–∏—è —Å–µ—Ç–µ–≤–æ–≥–æ –∑–∞–ø—Ä–æ—Å–∞
    await new Promise(resolve => setTimeout(resolve, 2000));

    // –ü—Ä–∏–º–µ—Ä–Ω—ã–µ –æ–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Ç–æ–≤–∞—Ä–æ–≤ (–≤ —Ä–µ–∞–ª—å–Ω–æ—Å—Ç–∏ –ø–æ–ª—É—á–∞–ª–∏—Å—å –±—ã –∏–∑ Google Sheets)
    const updatedProducts = [
      { id: '1', name: '–û–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–π –§–æ—Ä–¥ –¢—Ä–∞–Ω–∑–∏—Ç', length: 4300, width: 2100, height: 2050 },
      { id: '2', name: '–û–±–Ω–æ–≤–ª–µ–Ω–Ω–∞—è –§—É—Ä–∞ 20–º3', length: 4500, width: 2300, height: 2100 },
      { id: '3', name: '–ù–æ–≤—ã–π –ì–∞–∑–µ–ª—å Next', length: 3200, width: 1800, height: 1900 }
    ];

    // –û–±–Ω–æ–≤–ª—è–µ–º –ª–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
    setVehicleTypes(updatedProducts);
    localStorage.setItem('vehicleTypes', JSON.stringify(updatedProducts));
    localStorage.setItem('lastProductDataUpdate', new Date().toISOString());

    setUpdateStatus('success');
    setLastUpdateTime(new Date().toLocaleString('ru-RU'));
    setHasUnsavedChanges(false);

    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å–∫—Ä—ã–≤–∞–µ–º —Å—Ç–∞—Ç—É—Å —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
    setTimeout(() => setUpdateStatus('idle'), 3000);

  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö:', error);
    setUpdateStatus('error');
    setTimeout(() => setUpdateStatus('idle'), 5000);
  } finally {
    setIsUpdatingData(false);
  }
};

