    localStorage.setItem('lastProductDataUpdate', new Date().toISOString());

    setUpdateStatus('success');
    setLastUpdateTime(new Date().toLocaleString('ru-RU'));
    setHasUnsavedChanges(false);

    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å–∫—Ä—ã–≤–∞–µ–º —Å—Ç–∞—Ç—É—Å —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
    setTimeout(() => setUpdateStatus('idle'), 3000);

  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö:', error);
