// CRITICAL DEV CACHE CLEARING SCRIPT
// Очищает ВСЕ кроме данных формы пользователя
(function() {
  'use strict';

  console.log('[CACHE-CLEAR] Starting selective cache clearing...');

  // Сохраняем пользовательские данные формы перед очисткой
  let savedFormData = null;
  let savedAppVersion = null;
  try {
    if (typeof localStorage !== 'undefined') {
      // Сохраняем только данные формы и версию приложения
      savedFormData = localStorage.getItem('deliveryFormData');
      savedAppVersion = localStorage.getItem('appVersion');

      // Полная очистка localStorage
      localStorage.clear();
      console.log('[CACHE-CLEAR] localStorage cleared');

      // Восстанавливаем только пользовательские данные
      if (savedFormData) {
        localStorage.setItem('deliveryFormData', savedFormData);
        console.log('[CACHE-CLEAR] Form data restored');
      }
      if (savedAppVersion) {
        localStorage.setItem('appVersion', savedAppVersion);
        console.log('[CACHE-CLEAR] App version restored');
      }
    }
  } catch (e) {
    console.warn('[CACHE-CLEAR] localStorage selective clear failed:', e);
  }

  try {
    if (typeof sessionStorage !== 'undefined') {
      sessionStorage.clear();
      console.log('[CACHE-CLEAR] sessionStorage cleared');
    }
  } catch (e) {
    console.warn('[CACHE-CLEAR] sessionStorage clear failed:', e);
  }

  // Clear all caches
  if ('caches' in window) {
    caches.keys().then(function(cacheNames) {
      console.log('[CACHE-CLEAR] Found caches:', cacheNames);
      return Promise.all(
        cacheNames.map(function(cacheName) {
          console.log('[CACHE-CLEAR] Deleting cache:', cacheName);
          return caches.delete(cacheName);
        })
      );
    }).then(function() {
      console.log('[CACHE-CLEAR] All caches cleared');
    }).catch(function(error) {
      console.warn('[CACHE-CLEAR] Cache clearing failed:', error);
    });
  }

  // Unregister all service workers
  if ('serviceWorker' in navigator) {
    navigator.serviceWorker.getRegistrations().then(function(registrations) {
      console.log('[CACHE-CLEAR] Found service workers:', registrations.length);
      return Promise.all(
        registrations.map(function(registration) {
          console.log('[CACHE-CLEAR] Unregistering SW:', registration.scope);
          return registration.unregister();
        })
      );
    }).then(function() {
      console.log('[CACHE-CLEAR] All service workers unregistered');
    }).catch(function(error) {
      console.warn('[CACHE-CLEAR] SW unregistration failed:', error);
    });
  }

  console.log('[CACHE-CLEAR] Selective cache clearing completed');
})();
