        console.log('üîß DEV: ClientDevUtils –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω');
      } catch (error) {
        console.warn('üîß DEV: –û—à–∏–±–∫–∞ –≤ ClientDevUtils:', error);
      }
    }

    // Web Vitals monitoring
    if (typeof window !== 'undefined') {
      import('web-vitals').then(({ getCLS, getFID, getFCP, getLCP, getTTFB }) => {
        // Core Web Vitals
        getCLS((metric) => {
          console.log('üìä CLS (Cumulative Layout Shift):', metric.value);
          // Send to analytics/monitoring service
          if (typeof window !== 'undefined' && (window as any).gtag) {
            (window as any).gtag('event', 'web_vitals', {
              event_category: 'Web Vitals',
              event_label: 'CLS',
              value: Math.round(metric.value * 1000),
              custom_map: { metric_value: metric.value }
            });
          }
        });

        getFID((metric) => {
          console.log('üìä FID (First Input Delay):', metric.value, 'ms');
          if (typeof window !== 'undefined' && (window as any).gtag) {
            (window as any).gtag('event', 'web_vitals', {
              event_category: 'Web Vitals',
              event_label: 'FID',
              value: Math.round(metric.value),
              custom_map: { metric_value: metric.value }
            });
          }
        });

        getFCP((metric) => {
          console.log('üìä FCP (First Contentful Paint):', metric.value, 'ms');
          if (typeof window !== 'undefined' && (window as any).gtag) {
            (window as any).gtag('event', 'web_vitals', {
              event_category: 'Web Vitals',
              event_label: 'FCP',
              value: Math.round(metric.value),
              custom_map: { metric_value: metric.value }
            });
          }
        });

        getLCP((metric) => {
          console.log('üìä LCP (Largest Contentful Paint):', metric.value, 'ms');
          if (typeof window !== 'undefined' && (window as any).gtag) {
            (window as any).gtag('event', 'web_vitals', {
              event_category: 'Web Vitals',
              event_label: 'LCP',
              value: Math.round(metric.value),
              custom_map: { metric_value: metric.value }
            });
          }
        });

        getTTFB((metric) => {
          console.log('üìä TTFB (Time to First Byte):', metric.value, 'ms');
          if (typeof window !== 'undefined' && (window as any).gtag) {
            (window as any).gtag('event', 'web_vitals', {
              event_category: 'Web Vitals',
              event_label: 'TTFB',
              value: Math.round(metric.value),
              custom_map: { metric_value: metric.value }
            });
          }
        });
      }).catch((error) => {
        console.warn('‚ö†Ô∏è Failed to load web-vitals:', error);
      });
    }
  }, []);

  return null;
