          {isEnabled ? '–í–∫–ª' : '–í—ã–∫–ª'}
        </span>
        <Switch
          checked={isEnabled}
          onCheckedChange={(checked) => {
            setEnabledCompanies(prev => ({
              ...prev,
              [company.apiKey]: checked
            }));
          }}
          disabled={!isConnected}
        />
      </div>
    </div>
  );
})}

